import React from 'react';
import ReactDOM from 'react-dom/client';
import ForceGraph3D from 'react-force-graph-3d'
import SpriteText from "three-spritetext";
import './index.css';
import { useRef, useEffect } from "react";
import App from './App';
import reportWebVitals from './reportWebVitals';
import data from "./data.json";
import * as THREE from 'three';
import { UnrealBloomPass } from "three/examples/jsm/postprocessing/UnrealBloomPass";

const root = ReactDOM.createRoot(document.getElementById('graph'));

const FocusGraph = () => {

  const fgRef = useRef();

  useEffect(() => {
    const bloomPass = new UnrealBloomPass();
    bloomPass.strength = 0.5;
    bloomPass.radius = 1;
    bloomPass.threshold = 0;
    fgRef.current.postProcessingComposer().addPass(bloomPass);
  }, []);

  return (
    <ForceGraph3D
      ref={fgRef}
      backgroundColor="#000003"
      graphData={data}
      nodeAutoColorBy="group"
      // nodeThreeObject={(node) => {
      //   const sprite = new SpriteText(node.id);
      //   sprite.color = node.color;
      //   sprite.textHeight = 8;
      //   return sprite;
      // }}

      nodeLabel={(node) => `${node.description}`} // SENIOR QUOTES
      nodeThreeObjectExtend={true}
      nodeThreeObject={(node) => {
        // extend link with text sprite
        const sprite = new SpriteText(`${node.id}`);
        sprite.color = "lightgrey";
        sprite.textHeight = 5;
        // Adjust the position of the sprite to be below the node
        sprite.position.y -= 10; // Adjust this value as needed
        return sprite;
      }}

    />
  );
};




root.render(
  <React.StrictMode>
    <FocusGraph />
  </React.StrictMode>
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
