      // d3AlphaDecay={0.05}
      // d3VelocityDecay={0.2}
      // d3ForceLink={(link) => {
      //   link.distance = 30; // Adjust this value to make links shorter
      // }}
      // d3ForceCharge={(charge) => {
      //   charge.strength = -50; // Adjust this value to make nodes attract each other more
      // }}

      // IMGS 2D
      nodeThreeObject={(node) => {
        const group = new THREE.Group();

        // Create image sprite
        const imgTexture = new THREE.TextureLoader().load(node.img);
        const material = new THREE.SpriteMaterial({ map: imgTexture });
        const sprite = new THREE.Sprite(material);
        sprite.scale.set(12, 12);
        group.add(sprite);

        // Create text sprite
        const textSprite = new SpriteText(node.name);
        textSprite.color = "#ffffff";
        textSprite.textHeight = 5;
        textSprite.position.set(0, -15, 0); // Position text below the image
        group.add(textSprite);

        // Adjust the position of the sprite to be below the node
        sprite.position.y -= 10; // Adjust this value as needed

        // Check if bloom should be disabled for this node
        if (node.group === "TRACK") {
          // Change 'TRACK' to the specific group you want to turn off bloom
          sprite.layers.disable(1); // Disable bloom layer
        }

        return group;
      }}
      nodeCanvasObject={(node, ctx, globalScale) => {
        const img = new Image();
        img.src = node.img;
        const size = 12 / globalScale; // Adjust the size as needed

        img.onload = () => {
          ctx.drawImage(img, node.x - size / 2, node.y - size / 2, size, size);
        };

        node.__bckgDimensions = [size, size]; // to re-use in nodePointerAreaPaint
      }}
      nodePointerAreaPaint={(node, color, ctx) => {
        ctx.fillStyle = color;
        const bckgDimensions = node.__bckgDimensions;
        bckgDimensions &&
          ctx.fillRect(
            node.x - bckgDimensions[0] / 2,
            node.y - bckgDimensions[1] / 2,
            ...bckgDimensions
          );
      }}
      // IMGS 3D
