      //   textSprite.color = "#ffffff";
      //   textSprite.textHeight = 5;
      //   textSprite.position.set(0, -15, 0); // Position text below the image
      //   group.add(textSprite);

      //   return group;
      // }}
    />
  </React.StrictMode>
);

