import React from 'react';
import ReactDOM from 'react-dom/client';
import ForceGraph3D from 'react-force-graph-3d'
import SpriteText from "three-spritetext";
import './index.css';
import { useRef, useEffect } from "react";
import App from './App';
import reportWebVitals from './reportWebVitals';
import data from "./data.json";
import * as THREE from 'three';
import { UnrealBloomPass } from "three/examples/jsm/postprocessing/UnrealBloomPass";

const root = ReactDOM.createRoot(document.getElementById('graph'));

const FocusGraph = () => {
  const fgRef = useRef();

  useEffect(() => {
    if (fgRef.current) {
      const bloomPass = new UnrealBloomPass();
      bloomPass.strength = 0.5;
      bloomPass.radius = 1;
      bloomPass.threshold = 0;
      fgRef.current.postProcessingComposer().addPass(bloomPass);

      // Access and manipulate nodes to turn off bloom for specific group
      const nodes = fgRef.current.graphData().nodes;
      nodes.forEach((node) => {
        if (node.group === "TRACK") {
          node.__bloom = false; // Custom property to indicate bloom should be off
        }
      });
      fgRef.current.refresh();
    }
  }, [fgRef]);

  // Define a color mapping for groups
  const groupColors = {
    "P": "#ff0000", // Red
    "C": "#00ff00", // Green
    "E": "#0000ff", // Blue
  };

  return (
    <ForceGraph3D
      ref={fgRef}
      backgroundColor="#000003"
      graphData={data}
      nodeAutoColorBy="group"
      nodeColor={(node) => groupColors[node.group] || "#ffffff"} // Default to white if group not found
      // nodeThreeObject={(node) => {
      //   const sprite = new SpriteText(node.id);
      //   sprite.color = node.color;
      //   sprite.textHeight = 8;
      //   return sprite;
      // }}

      nodeLabel={(node) => `${node.description}`} // SENIOR QUOTES
      nodeThreeObjectExtend={true}
      // OLD CODE
      // nodeThreeObject={(node) => {
      //   // extend link with text sprite
      //   const sprite = new SpriteText(`${node.id}`);
      //   sprite.color = "lightgrey";
      //   sprite.textHeight = 5;
      //   // Adjust the position of the sprite to be below the node
      //   sprite.position.y -= 10; // Adjust this value as needed
      //   return sprite;
      // }}
      nodeThreeObject={(node) => {
        if (node.group === "TRACK") {
          // Change 'E' to the specific group you want to render as an image
          const imgTexture = new THREE.TextureLoader().load(node.img);
          const material = new THREE.SpriteMaterial({ map: imgTexture });
          const sprite = new THREE.Sprite(material);
          sprite.scale.set(12, 12);
          return sprite;
        } else {
          const sprite = new SpriteText(node.id);
          sprite.color = node.color || "lightgrey";
          sprite.textHeight = 5;
            // Adjust the position of the sprite to be below the node
            sprite.position.y -= 10; // Adjust this value as needed
            return sprite;
        }
      }}
      postProcessingComposer={(composer) => {
        // Remove bloom pass for nodes with __bloom set to false
        const bloomPass = composer.passes.find(
          (pass) => pass instanceof UnrealBloomPass
        );
        if (bloomPass) {
          bloomPass.enabled = !fgRef.current
            .graphData()
            .nodes.some((node) => node.__bloom === false);
        }
      }}
    />
  );
};




root.render(
  <React.StrictMode>
    <FocusGraph />
  </React.StrictMode>
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
