import React from "react";
import ReactDOM from "react-dom/client";
import ForceGraph2D from "react-force-graph-2d";
import "./index.css";
import reportWebVitals from "./reportWebVitals";
import data from "./data.json";

const root = ReactDOM.createRoot(document.getElementById("graph"));

const nodePaint = (node, ctx, globalScale) => {
  const img = new Image();
  img.src = node.img;
  const size = 12 / globalScale; // Adjust the size as needed

  img.onload = () => {
    ctx.drawImage(img, node.x - size / 2, node.y - size / 2, size, size);
  };

  // Draw the image immediately if it's already loaded
  if (img.complete) {
    ctx.drawImage(img, node.x - size / 2, node.y - size / 2, size, size);
  }

  node.__bckgDimensions = [size, size]; // to re-use in nodePointerAreaPaint
};

const nodePointerAreaPaint = (node, color, ctx) => {
  ctx.fillStyle = color;
  const bckgDimensions = node.__bckgDimensions;
  bckgDimensions &&
    ctx.fillRect(
      node.x - bckgDimensions[0] / 2,
      node.y - bckgDimensions[1] / 2,
      ...bckgDimensions
    );
};

root.render(
  <React.StrictMode>
    <ForceGraph2D
      backgroundColor="#000003"
      graphData={data}
      nodeLabel={(node) => `${node.description}`} // SENIOR QUOTES
      linkColor={() => "white"}
      onNodeClick={(node) => {
        if (node.url) {
          window.open(node.url, "_blank");
        }
      }}
      nodeCanvasObject={(node, ctx, globalScale) =>
        nodePaint(node, ctx, globalScale)
      }
      nodePointerAreaPaint={nodePointerAreaPaint}
    />
  </React.StrictMode>
);

reportWebVitals();
